import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from "react-redux";
import { updateCheckout } from '../redux/actions/attendance/attendanceaction';

const Checkout = () => {
    const history = useNavigate();
    const timer = JSON.parse(sessionStorage.getItem("time"));
    console.log("updateproduct", timer);

    const [checkoutime, setCheckOutTime] = useState("");
    const dispatch = useDispatch();
    // const time = null;
    // const [ctime, setDate] = useState(time);
    // let time = new Date().toLocaleTimeString();
    // setDate(time);
    const formSubmitHandler = (e) => {
        let time = new Date().toLocaleTimeString();
        setCheckOutTime(time);

        e.preventDefault();
        dispatch(updateCheckout(time));
        history("/get-student-data");
    };
    return (
        <div>
            <form onSubmit={formSubmitHandler}>
                <fieldset>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Product Name</label>
                        <input
                            value={checkoutime}
                            onChange={(e) => setCheckOutTime(e.target.value)}
                            type="text"
                            className="form-control"
                            id="exampleInputEmail1"
                            aria-describedby="emailHelp"
                            placeholder="Product Name"
                        />
                    </div>


                    <button type="submit" className="btn btn-primary m-auto">
                        Update your Products
                    </button>
                </fieldset>
            </form>
        </div>
    )

}

export default Checkout
